SELECT c.spg_cuenta, c.nivel, max(c.denominacion) as denominacion, sum(c.asignado) as asignado, sum(c.comprometido) as comprometido, sum(c.causado) as causado, sum(c.pagado) as pagado, sum(c.aumento) as aumento, sum(c.disminucion) as disminucion, sum(c.enero) as enero,  sum(c.febrero) as febrero, sum(c.marzo) as marzo, sum(c.abril) as abril, sum(c.mayo) as mayo, sum(c.junio) as junio, sum(c.julio) as julio, sum(c.agosto) as agosto, sum(c.septiembre) as septiembre, sum(c.octubre) as octubre, sum(c.noviembre) as noviembre, sum(c.diciembre) as diciembre, c.codestpro1,c.codestpro2,c.codestpro3,c.codestpro4,c.codestpro5,c.estcla  FROM  spg_cuentas c, spg_ep3 EP WHERE  c.codemp='0001'  AND CONCAT(c.codestpro1,c.codestpro2,c.codestpro3,c.codestpro4,c.codestpro5,c.estcla) between '00000000000000000000000010000000000000000000000001000000000000000000000005100000000000000000000000000000000000000000000000000P' AND '00000000000000000000000010000000000000000000000001000000000000000000000005100000000000000000000000000000000000000000000000000P' AND EP.codfuefin BETWEEN '--' AND '10' GROUP BY c.spg_cuenta, c.nivel, c.codestpro1, c.codestpro2, c.codestpro3, c.codestpro4, c.codestpro5, c.estcla ORDER BY c.spg_cuenta
